import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import red from '@material-ui/core/colors/red';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import {MDBNavbarNav, MDBIcon,MDBNavLink,MDBNavItem } from "mdbreact";
import { Container, Row, Col, Mask, Fa, View, Button} from 'mdbreact';

const styles = theme => ({
  card: {
    width: '85%',
    marginLeft: "auto",
    marginRight: "auto",
    marginTop: 20,
  },
  avatar: {
    backgroundColor: red[500],
  },
});

class ProductCotent extends React.Component {
  state = { expanded: false };

  render() {
    const { classes } = this.props;

    return (
      <Card className={classes.card}>
       <h3 className="font-weight-bold mb-3 p-0" style={{display:'inline-block', marginTop:10, marginLeft:10}}><strong>MÔ TẢ SẢN PHẨM </strong></h3>
       <p>Bộ Kit cáp MHL to HDMI cho điện thoại Android.
Thông tin sản phẩm:
Bộ sản phẩm gồm: 1 cáp nối với điện thoại, 1 cổng sạc cấp nguồn, 1 ra HDMI, 1 cáp 5 pin, 1 cáp 11 pin.
Cáp MHL micro USB sang HDMI đa năng cho điện thoại. VD: Samsung, LG, Sky, Sony, HTC dùng để xuất hình ảnh. Chuẩn HDMI 1.4.
Hướng dẫn sử dụng:

Cáp MHL to HDMI phải được cung cấp điện hỗ trợ bên ngoài.
Sau khi cắm nguồn cung cấp điện cho cáp MHL. Bạn phải khởi động lại điện thoại để xuất hình HDMI.
Cáp MHL không cần bất kì cài đặt nào, sau khi khởi động lại thiết bị chạy android, tự xuất ra màn hình HDMI.
Hỗ trợ tất cả các tính năng ưu việt của đầu ra 1080P (âm thanh và hình ảnh tuyệt vời).
Chuẩn MHL là một tiêu chuẩn được phổ biến cho điện thoại hoặc máy tính bẳng chạy android xuất ra màn hình lớn chuẩn HDMI (Miễn là điện thoại hỗ trợ MHL).
Hỗ trợ các dòng máy:
– Samsung Galaxy S2.S3.S4.Note 2. I9100. I9108. i9188. I997, Galaxy Note (i9220), i9250, LT26i, Note2 N7100 Tab 3, hoặc cao hơn…
– Sky A800, A820, A830, hoặc cao hơn…
– Nokia N10.
– Sony Xperia Z C6602, C6603,….
– HTC Sensation (HTC G14). HTC Flyer (P510E). HTC EVO 3D ( G17). Z710, G14, Z710e, Z710t, HTC SensationXE Z715 (G18), HTC one. One M7. One M8. HTC one X. hoặc cao hơn…
– LG LTE2, G Pro, G, GX, GK, G2, G3…
Hàng bảo hành: 6 tháng.

Chúng tôi chuyên phân phối các sản phẩm nhập khẩu. Quý khách hàng có nhu cầu mua buôn, mua sỉ vui lòng liên hệ qua khung chát trên gian hàng hoặc hotline: 01673852222</p>
      </Card>
    );
  }
}

ProductCotent.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ProductCotent);
